function PlotGraph(pos_opt1,pos_opt2,pos_opt3, titre, simnum)    longuerTerrain = 60;    largeurTerrain = 70;    hauteurTerrain = 0;        x1 = pos_opt1(:,1);    y1 = pos_opt1(:,2);    z1 = pos_opt1(:,3);        x2 = pos_opt2(:,1);    y2 = pos_opt2(:,2);    z2 = pos_opt2(:,3);        x3 = pos_opt3(:,1);    y3 = pos_opt3(:,2);    z3 = pos_opt3(:,3);        circle_center_x =  92;    circle_center_y =  53;    rayon_vert = 15;    hauteur_vert = 3.5;        Rayon_sphere = ( rayon_vert^2 + hauteur_vert^2) / (2*hauteur_vert);        sphere_center_z = - ( Rayon_sphere - hauteur_vert);    rayon_trou = 0.054;        figure(simnum, 'Name', titre) ; clf;    hold on;        outer = [-20 -20; 130 -20; 130 90; -20 90];    inner = [0 0 0.001; 60 0 0.001; 110 45 0.001;110 70 0.001; 0 70 0.001];    patch(outer(:, 1), outer(:, 2), [0.5451,0.271,0.07451]);    hold on;    patch(inner(:, 1), inner(:, 2),inner(:, 3), 'g');    hold on;          h = drawSphere([circle_center_x circle_center_y sphere_center_z Rayon_sphere]);    set(h, 'edgecolor', 'none');    set(h, 'facecolor', [0,0.3921,0]);    hold on;                [x, y, z] = cylinder ([rayon_trou rayon_trou], 50);    surf (x+circle_center_x, y+circle_center_y, z+hauteur_vert);    hold on;        ##    coords_trou = circleAsPolygon([circle_center_x, circle_center_y, rayon_trou]);##    drawPolygon(coords_trou(:, 1), coords_trou(:, 2), [1,1,1]);##    fillPolygon(coords_trou(:, 1), coords_trou(:, 2), [1,1,1]);##    hold on;    plot3(x1 ,y1, z1, 'linewidth', 1.8, 'r');    hold on  ;    plot3(x2, y2, z2, 'linewidth', 1.8, 'b');    hold on;    plot3(x3, y3, z3, 'linewidth', 1.8, 'k');        title(titre);     legend('Bois', 'Terrain', 'Vert', 'Trou','Option 1', 'Option 2', 'Option 3');##    xlim([-50 150]);##    ylim([-20 100]);##    zlim([-80 20]);    xlabel('X');    ylabel('Y');    zlabel('Z');    end##    BoiseZZ = [0 0; 0 0];##    ##    %bois gauche##    ##    