function [coup] = VerifierConditions(pos)  % coup = 0: vous avez reussi un trou dâ€™un coup  % coup = 1: la balle a atteint le vert  % coup = 2: la balle est sur le terrain apres le coup sans atteindre le vert  % coup = 3: la balle sort du terrain    coup = -1;    r = 0.02135;    y_max = 70;  y_min = 0;    % verifier si la balle dans le triangle boise  function [estDansBoise] = fonctionTriangeBoise(x,y)    res =  (9/10)*x - 54;    estDansBoise = y < res;  endfunction      % verifier sur vert en x et y    function [estDansVertEnXY] = fonctionVertXY(x,y)      coord_centre = [110-18,70-17];      rayon = 15;      estDansVertEnXY = (x-coord_centre(1))^2 + (y-coord_centre(2))^2 <= rayon^2;  endfunction      function [toucheVert] = fonctionToucheVert(x,y,z)        r = 15;    h = 3.5;        R = ( r^2 + h^2) / 2*h;    coord_centre = [110-18,70-17, -(R-3.5)];        toucheVert = (x-coord_centre(1))^2 + (y-coord_centre(2))^2 + (z-coord_centre(3))^2 <= (R+0.001)^2 || ...                 (x-coord_centre(1))^2 + (y-coord_centre(2))^2 + (z-coord_centre(3))^2 <= (R-0.001)^2;      endfunction    function [estDansTrou] = fonctionDansTrou(x,y,z)        coord_centre = [110-18,70-17, 3.5];        rayon = 0.054;        estDansTrou = (((x-coord_centre(1))^2 + (y-coord_centre(2))^2 <= (rayon-0.001)^2) || ...                  ((x-coord_centre(1))^2 + (y-coord_centre(2))^2 <= (rayon+0.001)^2)) && ...                  (coord_centre(3)-0.001 <= z || z <= coord_centre(3)+0.001);      endfunction      x_max = 110;  x_min = 0;      x = pos(1);  y = pos(2);  z = pos(3);      if(y > y_max || y < y_min || fonctionTriangeBoise(x,y) || x > x_max || x < x_min)    % la balle sort du terrain    coup = 3;  elseif (fonctionVertXY(x,y))    % la balle est dans la region vert    if (fonctionToucheVert(x,y,z))      % la balle touche la region vert      if (fonctionDansTrou(x,y,z))        % la balle est dans le trou        coup = 0;      else        coup = 1;      endif    endif  else    if(abs(z - r) <= 0.001)    % la balle est sur le terrain mais pas dans le vert      coup = 2;    endif  endif  endfunction