function PlotGraph(all_pos_sommets,all_pos_balle,sim_num)    rayon_balle = 0.02;    figure('Name', strcat("Simulation",sim_num)) ;  hold on;    [n f] = createCube;    pas = 1;    if size(all_pos_sommets)(2) > 100    pas = 10;  endif    for i=1:pas:size(all_pos_sommets)(2)    n = cell2mat(all_pos_sommets(:,i));    drawPolyhedron(n, f);    hold on;  endfor    for j=1:pas:size(all_pos_balle)(1)    h = drawSphere([all_pos_balle(j,1) all_pos_balle(j,2) all_pos_balle(j,3) rayon_balle]);    set(h, 'edgecolor', 'none');    set(h, 'facecolor', [0,0.3921,0]);    hold on;  endfor    if i != size(all_pos_sommets)(2)    n = cell2mat(all_pos_sommets(:,size(all_pos_sommets)(2)));    drawPolyhedron(n, f);    hold on;  endif    if j != size(all_pos_balle)(1)    j = size(all_pos_balle)(1);    h = drawSphere([all_pos_balle(j,1) all_pos_balle(j,2) all_pos_balle(j,3) rayon_balle]);    set(h, 'edgecolor', 'none');    set(h, 'facecolor', [0,0.3921,0]);    hold on;  endif     xlabel('X');   ylabel('Y');   zlabel('Z');endfunction