function [polMax polMin azimMax azimMin] = TrouverAngles(pos)    Rayon = 3;  Hauteur = 9;    vecteurZ = [0, 0, 1];    posObsXY = [pos(1),pos(2)];  posEllipsoid = [4, 4, 11];  posEllipsoidXY = [4, 4];    vecEllipsoid = posEllipsoidXY - posObsXY;  vecOrthEllipsoid = [1, -1];  vecOrthEllipsoid = vecOrthEllipsoid / norm(vecOrthEllipsoid);    pointSurface = vecEllipsoid - ((Rayon * vecEllipsoid) / norm(vecEllipsoid));    axeX = [1,0];  axeZ = [0,0,1];      pointMax = pointSurface + (Rayon * vecOrthEllipsoid);  pointMin = pointSurface - (Rayon * vecOrthEllipsoid);    azimMax = acos(dot(pointMin, axeX) / (norm(pointMin) * norm(axeX)));    phi = acos(dot(pointMax, pointMin) / (norm(pointMax) * norm(pointMin)));    azimMin =  azimMax - phi;    pointHaut = (posEllipsoid + [-Rayon,0,Hauteur]) - pos;  pointBas = (posEllipsoid + [-Rayon,0,-Hauteur]) - pos;   polMin = acos(dot(pointHaut, axeZ) / (norm(pointHaut) * norm(axeZ)));    theta = acos(dot(pointHaut, pointBas) / (norm(pointHaut) * norm(pointBas)));    polMax = acos(dot(axeZ, pointBas) / (norm(axeZ) * norm(pointBas)));      
endfunction
