function [Touche intersectionPoint distanceToPlane] = intersectPlaneLine(depart,rayon_vec,p1,p2,p3) minX = 3; maxX = 4; minY = 3; maxY = 5; minZ = 12; maxZ = 17; p01 = p2 - p1; p02 = p3 - p1; det = dot(-rayon_vec,cross(p01,p02));  Touche = 0; distanceToPlane = NaN; intersectionPoint = NaN;  if det != 0      t = dot(cross(p01,p02),(depart - p1))/det;      intersectionPoint = depart + t*rayon_vec;   correctX = intersectionPoint(1) <= maxX && intersectionPoint(1) >= minX;   correctY = intersectionPoint(2) <= maxY && intersectionPoint(2) >= minY;   correctZ = intersectionPoint(3) <= maxZ && intersectionPoint(3) >= minZ;      if correctX && correctY && correctZ        Touche = 1;     distanceToPlane = norm(intersectionPoint - depart);   else      intersectionPoint = NaN;   end    endendfunction