function [IntersectionPoint face] = intersectRectangleLine(depart,rayon_vec)      p1 = [3,3,12];    p2 = [3,3,17];    p3 = [3,5,12];    p4 = [3,5,17];    p5 = [4,3,12];    p6 = [4,3,17];    p7 = [4,5,12];    p8 = [4,5,17];        # plan 1 y-z x=3 rouge f=1    [Touche1 intersectionPoint1 distanceToPlane1] = intersectPlaneLine(depart,rayon_vec,p1,p2,p3);    # plan 2 y-z x=4 cyan f=2    [Touche2 intersectionPoint2 distanceToPlane2] = intersectPlaneLine(depart,rayon_vec,p5,p6,p7);    # plan 3 z-x y=3 vert f=3    [Touche3 intersectionPoint3 distanceToPlane3] = intersectPlaneLine(depart,rayon_vec,p1,p2,p5);    # plan 4 z-x y=5 jaune f=4    [Touche4 intersectionPoint4 distanceToPlane4] = intersectPlaneLine(depart,rayon_vec,p3,p4,p7);    # plan 5 x-y z=12 bleue f=5    [Touche5 intersectionPoint5 distanceToPlane5] = intersectPlaneLine(depart,rayon_vec,p1,p3,p5);    # plan 6 x-y z=17 magenta f=6    [Touche6 intersectionPoint6 distanceToPlane6] = intersectPlaneLine(depart,rayon_vec,p4,p2,p8);        IntersectionPoint = NaN;    distanceMinimale = inf;    face = NaN;        if Touche1      distanceMinimale = distanceToPlane1;      IntersectionPoint = intersectionPoint1;      face = 1;    end        if Touche2      if distanceToPlane2 < distanceMinimale          distanceMinimale = distanceToPlane2;          IntersectionPoint = intersectionPoint2;          face = 2;      end    end        if Touche3      if distanceToPlane3 < distanceMinimale          distanceMinimale = distanceToPlane3;          IntersectionPoint = intersectionPoint3;          face = 3;      end    end        if Touche4      if distanceToPlane4 < distanceMinimale          distanceMinimale = distanceToPlane4;          IntersectionPoint = intersectionPoint4;          face = 4;      end    end        if Touche5      if distanceToPlane5 < distanceMinimale          distanceMinimale = distanceToPlane5;          IntersectionPoint = intersectionPoint5;          face = 5;      end    end        if Touche6      if distanceToPlane6 < distanceMinimale          distanceMinimale = distanceToPlane6;          IntersectionPoint = intersectionPoint6;          face = 6;      end    end        
endfunction
