function [x y] = intersectionEllipsoidLigne(pos,rayon_vec,posEllipsoid)    a = 3;  b = 3;  c = 9;    disp(rayon_vec);    comp2 = [pos(1)-posEllipsoid(1),pos(2)-posEllipsoid(2),pos(3)-posEllipsoid(3)];    a = (9*(rayon_vec(1)^2))+(9*(rayon_vec(2)^2))+(rayon_vec(3)^2);  b = (9*2*rayon_vec(1)*(comp2(1))) + (9*2*rayon_vec(2)*(comp2(2))) + (2*rayon_vec(3)*(comp2(3)));  c = 9*comp2(1)^2 + 9*comp2(2)^2 + comp2(3)^2 - 81;##  xDiff = lineX - circleX;##  yDiff = lineY - circleY;##  a = lineVX * lineVX + lineVY * lineVY;##  b = 2 * (lineVX * (lineX - circleX) + lineVY * (lineY - circleY));##  c = xDiff * xDiff + yDiff * yDiff - r * r;  disc = (b * b) - (4 * a * c);    disp(disc);    x = 0;  y = 0;  if disc >= 0     t1 = (-b - sqrt(disc)) / (2 * a);      t2 = (-b + sqrt(disc)) / (2 * a);        x1 = pos + (t1 * rayon_vec);    scatter3(x1(1),x1(2),x1(3));    hold on;        x2 = pos + (t2 * rayon_vec);    scatter3(x2(1),x2(2),x2(3));    hold on;##    x = lineX + lineVX * t;##    y = lineY + lineVY * t;  endifendfunction