function [point_intersection] = intersectionEllipsoidLigne(pos,rayon_vec,posEllipsoid,rayon_dehors)    a = 3;  b = 3;  c = 9;    point_intersection = NaN;    comp2 = [pos(1)-posEllipsoid(1),pos(2)-posEllipsoid(2),pos(3)-posEllipsoid(3)];    a = (9*(rayon_vec(1)^2))+(9*(rayon_vec(2)^2))+(rayon_vec(3)^2);  b = (9*2*rayon_vec(1)*(comp2(1))) + (9*2*rayon_vec(2)*(comp2(2))) + (2*rayon_vec(3)*(comp2(3)));  c = 9*comp2(1)^2 + 9*comp2(2)^2 + comp2(3)^2 - 81;  disc = (b * b) - (4 * a * c);    if disc >= 0    if rayon_dehors      t = (-b - sqrt(disc)) / (2 * a);    else      t = (-b + sqrt(disc)) / (2 * a);    endif        point_intersection = pos + (t * rayon_vec);  endifendfunction